import React from "react";
import styled, { css } from "styled-components";
import { GiHamburgerMenu } from "react-icons/gi";
import { GrClose } from "react-icons/gr";
import TimelineContainer from "../container/TimelineContainer";

import timelineIcon from "../assets/timeline-icon.png";

function HeaderPresenter ({
  Home,
  About,
  Skills,
  Project,
  pos,
  mode,
  open,
  toggleHamburger,
  sidebar,
  setSidebar,
}) {
  return (
    <>
      <TimelineContainer sidebar={sidebar} setSidebar={setSidebar} />

      <HeaderContainer pos={pos} mode={mode}>
        <Title mode={mode}>
          NEXT x Likelion
        </Title>

        <HamburgerBtn mode={mode} onClick={toggleHamburger}>
          {!open ? <GiHamburgerMenu size="24" /> : <GrClose size="24" />}
        </HamburgerBtn>

        <Cover mode={mode} />

        <LinkContainer mode={mode} open={open}>
          <LinkBtn mode={mode} current={pos <= Home.bottom}>
            <LinkItem href="#Home">홈페이지</LinkItem>
          </LinkBtn>
          <LinkBtn
            mode={mode}
            current={pos >= About.top && pos <= About.bottom}
          >
            <LinkItem href="#About">프로필</LinkItem>
          </LinkBtn>
          <LinkBtn
            mode={mode}
            current={pos >= Skills.top && pos <= Skills.bottom}
          >
            <LinkItem href="#Skills">기술 스택</LinkItem>
          </LinkBtn>
          <LinkBtn mode={mode} current={pos >= Project.top}>
            <LinkItem href="#Project">프로젝트</LinkItem>
          </LinkBtn>

          <TimelineBtn onClick={() => setSidebar(true)} mode={mode}>
            <img src={timelineIcon} alt="timeline icon" style={{ width: '30px' }} />
          </TimelineBtn>

        </LinkContainer>
      </HeaderContainer>
    </>
  );
}

const HeaderContainer = styled.div`
  background: white;
  position: fixed;
  right: 0;
  left: 0;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 5;

  ${(props) =>
    props.mode === "isMobile" &&
    css`
      padding: 10px 0 10px 0;
    `}

  ${(props) =>
    props.pos &&
    css`
      box-shadow: 0 1px 10px 1px rgba(0, 0, 0, 0.3);
      transition: box-shadow 0.3s ease-in;
    `}
`;

const Title = styled.h1`
  font-size: 24px;
  z-index: 1;
  margin-left: ${(props) => (props.mode === "isMobile" ? "25px" : "50px")};
`;

const P = styled.h1`
  display: inline;
  font-size: 24px;
  z-index: 1;
  color: red;
  text-decoration: underline;
  text-decoration-color: red;
  font-weight: bold;
`;

const LinkContainer = styled.ul`
  ${(props) =>
    props.mode === "isPc"
      ? css`
          display: flex;
          align-items: center;
          list-style: none;
          margin: 0 1rem 0 0;
        `
      : css`
          padding-left: 0;
          text-align: center;
          display: block;
          position: absolute;
          top: ${(props) => (props.open ? "100% " : "-820%")};
          transition: top 0.3s ease-in;

          width: 100%;
          background: white;
          text-align: -webkit-center;
          z-index: -1;
        `};
`;

const HamburgerBtn = styled.button`
  display: none;

  ${(props) =>
    props.mode === "isMobile" &&
    css`
      display: flex;
      border-radius: 8px;
      background: white;
      margin-right: 1rem;
      z-index: 1;
      border: none;

      &:active {
        filter: brightness(80%);
      }
    `}
`;

const TimelineBtn = styled.button`
  ${(props) =>
    props.mode === "isPc" &&
    css`
      margin-left: 20px;
    `}

  ${(props) =>
    props.mode === "isMobile" &&
    css`
      margin: 1rem 0 1rem 0;
      width: 75%;
      display: block;
    `}

  color: black;
  background: white;
  border: 0;
  outline: 0;
  cursor: pointer;
  border-radius: 1rem;
  padding: 0.5rem;

  &:active {
    filter: brightness(80%);
  }
`;

const LinkBtn = styled.li`
  list-style: none;
  cursor: pointer;
  padding: 1rem;

  font-size: 20px;
  ${(props) =>
    props.mode === "isPc" &&
    css`
      & + & {
        margin-left: 20px;
      }
      border-bottom: 3px solid
        ${(props) => (props.current ? "red" : "white")};

      transition: border-bottom 0.2s ease-in;
    `}

  ${(props) =>
    props.mode === "isMobile" &&
    css`
      background: white;
      margin: 1rem 0 1rem 0;

      width: 75%;
      border-radius: 1rem;

      &:active {
        filter: brightness(80%);
      }
    `}
`;

const LinkItem = styled.a`
  color: black;
  display: block;

  &:hover {
    color: black;
    text-decoration: none;
  }
`;

const Cover = styled.div`
  ${(props) =>
    props.mode === "isMobile" &&
    css`
      position: absolute;
      width: 100%;
      height: 100%;
      background-color: white;
      z-index: 0;
    `}
`;

export default HeaderPresenter;
