import React, { useState, useEffect } from "react";
import Timeline from "../components/Timeline";

import { useMediaQuery } from "react-responsive";

function TimelineContainer({ sidebar, setSidebar }) {
  const isPc = useMediaQuery({
    query: "(min-width:768px)",
  });
  const isMobile = useMediaQuery({
    query: "(max-width:767px)",
  });
  const timeLine = [
    {
      id: 0,
      date: "2018.02 - present",
      title: "Korea University",
      content: "고려대학교 보건과학대학 입학",
      category: "univ",
    },

    {
      id: 1,
      date: "2018.03 - 2018.12",
      title: "보건환경융합과학부 총학생회",
      content:
        "보건환경융합과학부 총학생회 기획국원으로 활동함",
      category: "univ2",
    },

    {
      id: 2,
      date: "2018.05 - present",
      title: "TTP",
      content: "고려대학교 중앙피아노동아리 TTP 가입",
      category: "ttp",
    },

    {
      id: 3,
      date: "2018.09 - 2019.06",
      title: "clo.Z",
      content:
        "고려대학교 중앙패션동아리 clo.Z 가입",
      category: "cloZ",
    },

    {
      id: 4,
      date: "2020.03 - 2021.09",
      title: "Military Service",
      content:
        "대한민국 육군 8군수지원단 병장 만기전역",
      category: "Army",
    },

    {
      id: 5,
      date: "2021.09 - 2021.12",
      title: "2022학년도 수능 재도전",
      content:
        "개같이 멸망",
      category: "mentor",
    },

    {
      id: 6,
      date: "2020.10 - 2022.02",
      title: "System Software Lab",
      content: "시스템 소프트웨어 연구실 연구원 활동",
      category: "lab",
    },

    {
      id: 7,
      date: "2020.12 - 2021.02",
      title: "System Software Lab",
      content:
        "레지던트 프로그램 활동을 통하여 연구실 내 연구과제, 산학과제, 특수 장비 관리, 산업체 기술지도 등의 다양한 활동을 참여하여 전공분야 역량 강화.",
      category: "lab",
    },

    {
      id: 8,
      date: "2021.02 - 2021.03",
      title: "셈틀꾼 멘토(JavaScript)",
      content:
        "컴퓨터공학과 학술동아리 '셈틀꾼'에서 전 회원들을 대상으로 자바스크립트 멘토를 진행함.",
      category: "mentor",
    },

    {
      id: 9,
      date: "2021.03 - 2021.06",
      title: "셈틀꾼 멘토(HTML, CSS, JS)",
      content:
        "컴퓨터공학과 학술동아리 '셈틀꾼'에서 전 회원들을 대상으로 웹 프로그래밍 멘토를 진행함.",
      category: "mentor",
    },

    {
      id: 10,
      date: "2021.03 - 2021.07",
      title: "YouCanDoEat Project",
      content: "외국인 대상 식자재 정보 제공 웹앱 구현 프로젝트 진행.",
      category: "project",
    },
  ];

  const [mode, setMode] = useState("");

  useEffect(() => {
    if (isPc) setMode("isPc");
    else if (isMobile) setMode("isMobile");
  }, [isMobile, isPc]);

  return (
    <Timeline
      sidebar={sidebar}
      setSidebar={setSidebar}
      timeLine={timeLine}
      mode={mode}
    />
  );
}

export default TimelineContainer;
